d_jurusan);$this->Model_jurusan->hapus_datajurusan($where,'tb_jurusan');$this->session->set_flashdata('pesan','<div class="alert alert-danger">Berhasil Hapus Data Jurusan..</div>');redirect('Jurusan/index','refresh');}} ?>