<?php defined('BASEPATH')||exit('No direct script access allowed');class Kelas extends CI_controller{public function __construct(){parent::__construct();$this->load->model('Model_kelas');}function index(){$t['title']=' | Data kelas ';$data['datas']=$this->Model_kelas->tampilkelas()->result();$this->load->view('templates/backend/header',$t);$this->load->view('templates/backend/sidebar');$this->load->view('backend/kelas/data',$data);$this->load->view('templates/backend/footer');}public function tambah(){$t['title']=' | Tambah Data kelas';$this->load->view('templates/backend/header',$t);$this->load->view('templates/backend/sidebar');$this->load->view('backend/kelas/tambah');$this->load->view('templates/backend/footer');}public function sv_kelas(){$this->form_validation->set_message('required','Kolom {field} Harus Diisi');$this->form_validation->set_rules('kelas','kelas','trim|required');if($this->form_validation->run()==FALSE){echo "gagal Input Data";}else{$this->Model_kelas->input_datakelas();$this->session->set_flashdata('pesan','<div class="alert alert-success">Berhasil Input Data kelas..</div>');redirect('kelas/index','refresh');}}public function edit($id_kelas){$t['title']=' | Edit Data kelas ';$where=array('id_kelas'=>$id_kelas);$data['tb_kelas']=$this->Model_kelas->edit_data_kelas($where,'tb_kelas')->result();$this->load->view('templates/backend/header',$t);$this->load->view('templates/backend/sidebar');$this->load->view('backend/kelas/edit',$data);$this->load->view('templates/backend/footer');}public function sav_kelas($id_kelas){$where=array('id_kelas'=>$id_kelas);$data=array('kelas'=>$this->input->post('kelas'),);$this->Model_kelas->edit_datakelas($where,$data,'tb_kelas');$this->session->set_flashdata('pesan','<div class="alert alert-success">Berhasil Edit Data kelas..</div>');redirect('kelas/index','refresh');}public function hapus($id_kelas){$where=array('id_kelas'=>$id_kelas);$this->Model_kelas->hapus_datakelas($where,'tb_kelas');$this->session->set_flashdata('pesan','<div class="alert alert-danger">Berhasil Hapus Data kelas..</div>');redirect('kelas/index','refresh');}} ?>