<?php class Model_dashboard extends CI_Model{public function total_users(){return $this->db->get('tb_users');}public function total_siswa(){$this->db->where(['level'=>'siswa']);return $this->db->get('tb_users');}public function total_guru(){$this->db->where(['level'=>'guru']);return $this->db->get('tb_users');}public function total_tabungan(){return $this->db->get('tb_tabungan');}public function total_pinjaman(){return $this->db->get('tb_pinjaman');}function tab_masuk(){$this->db->select_sum('jumlah_nabung');$sql=$this->db->get('tb_tabungan')->row();if($sql->jumlah_nabung==""){$masuk=0;}else{$masuk=$sql->jumlah_nabung;}return $masuk;}function tab_keluar(){$this->db->select_sum('jumlah_ambil');$sql=$this->db->get('tb_tabungan')->row();if($sql->jumlah_ambil==""){$keluar=0;}else{$keluar=$sql->jumlah_ambil;}return $keluar;}function pin_keluar(){$this->db->select_sum('jumlah_pinjam');$sql=$this->db->get('tb_pinjaman')->row();if($sql->jumlah_pinjam==""){$keluar=0;}else{$keluar=$sql->jumlah_pinjam;}return $keluar;}function pin_masuk(){$this->db->select_sum('jumlah_bayar');$sql=$this->db->get('tb_pinjaman')->row();if($sql->jumlah_bayar==""){$masuk=0;}else{$masuk=$sql->jumlah_bayar;}return $masuk;}}