<?php class Model_jurusan extends CI_Model{public function tampiljurusan(){return $this->db->get('tb_jurusan');}public function input_datajurusan(){$data=['jurusan'=>$this->input->post('jurusan'),];$data=$this->security->xss_clean($data);$this->db->insert('tb_jurusan',$data);}public function edit_data_jurusan($where,$table){return $this->db->get_where($table,$where);}public function edit_datajurusan($where,$data,$table){$this->db->where($where);$this->db->update($table,$data);}public function hapus_datajurusan($where,$table){$this->db->where($where);$this->db->delete($table);}}