<?php class Model_kelas extends CI_Model{public function tampilkelas(){return $this->db->get('tb_kelas');}public function input_datakelas(){$data=['kelas'=>$this->input->post('kelas'),];$data=$this->security->xss_clean($data);$this->db->insert('tb_kelas',$data);}public function edit_data_kelas($where,$table){return $this->db->get_where($table,$where);}public function edit_datakelas($where,$data,$table){$this->db->where($where);$this->db->update($table,$data);}public function hapus_datakelas($where,$table){$this->db->where($where);$this->db->delete($table);}}