<?php class Model_periode extends CI_Model{public function tampilpe(){return $this->db->get('tb_periode');}public function input_datape(){$data=['tahun_akademik'=>$this->input->post('tahun_akademik'),'semester'=>$this->input->post('semester'),'status_periode'=>$this->input->post('status_periode'),'periode_awal'=>$this->input->post('periode_awal'),'periode_akhir'=>$this->input->post('periode_akhir'),];$data=$this->security->xss_clean($data);$this->db->insert('tb_periode',$data);}public function edit_data_periode($where,$table){return $this->db->get_where($table,$where);}public function edit_dataperiode($where,$data,$table){$this->db->where($where);$this->db->update($table,$data);}public function hapus_dataperiode($where,$table){$this->db->where($where);$this->db->delete($table);}}