<div class="app-main__outer"><div class="app-main__inner"><div class="col-lg-4"><?php echo $this->session->flashdata('pesan'); ?></div><h5>Detail Piutang</h5><div class="row"><div class="col-lg-12"><div class="card main-card mb-3"><div class="card-body"><h5 class="card-title"><div><table width="100%"><tr><td width="20%">Nama</td><td width="2%">:</td><td><?=$identitas->nama?></td></tr><tr><td>No. Telepon</td><td>:</td><td><?=$identitas->telepon?></td></tr><tr><td>No. Rekening</td><td>:</td><td><?=$identitas->norek?></td></tr><tr><td>Alamat</td><td>:</td><td><?=$identitas->alamat?></td></tr></table></div><br><div class="search-wrapper"><div class="row"><div class="col-md-12"><p>Pencetakan Data</p><?php echo form_open('excelpinjaman?id='.$this->input->get('id')) ?><div class="form-row"><div class="col-md-3"><div class="form-group position-relative"><input class="form-control"name="k1"placeholder="Nama Anggota.."required type="date"></div></div><div class="col-md-3"><div class="form-group position-relative"><input class="form-control"name="k2"placeholder="Nama Anggota.."required type="date"></div></div><div class="col-md-3"><div class="form-group position-relative"><button class="btn btn-primary btn-sm"type="submit">Export Data <i class="fa fa-download"></i></button></div></div></div><?php echo form_close(); ?></div></div></div></h5><div class="row"><div class="col-md-12"><div class="table-responsive"><table class="mb-0 table table-bordered table-hover"><thead><tr><th style="font-size:12px">NO</th><th style="font-size:12px">WAKTU</th><th style="font-size:12px">URAIAN</th><th style="font-size:12px">DEBET</th><th style="font-size:12px">KREDIT</th><th style="font-size:12px">SALDO</th></tr></thead><tbody><?php $i=1;foreach($pinjaman as $row){if($row->kode=="2"){$ket='Bayar piutang';}else{$ket='Piutang masuk';} ?><tr><td style="font-size:12px"><?php echo $i; ?></td><td style="font-size:12px"><?php echo substr($row->waktu,0,10); ?></td><td style="font-size:12px"><?php echo $ket; ?></td><?php if($row->kode=="2"){$kredit=number_format($row->jumlah_bayar,0,",",".");$debet='';}else{$debet=number_format($row->jumlah_pinjam,0,",",".");$kredit='';} ?><td style="font-size:12px"class="text-right"><?=$debet?></td><td style="font-size:12px"class="text-right"><?=$kredit?></td><?php $this->db->select_sum('jumlah_bayar');$where=['id_user'=>$row->id_user,'waktu <='=>$row->waktu];$this->db->where($where);$msk=$this->db->get('tb_pinjaman')->row();if(empty($msk->jumlah_bayar)){$kredit2=0;}else{$kredit2=$msk->jumlah_bayar;}$this->db->select_sum('jumlah_pinjam');$where=['id_user'=>$row->id_user,'waktu <='=>$row->waktu];$this->db->where($where);$klr=$this->db->get('tb_pinjaman')->row();if(empty($klr->jumlah_pinjam)){$debet2=0;}else{$debet2=$klr->jumlah_pinjam;}$saldo=$debet2-$kredit2; ?><td style="font-size:12px"class="text-right"><?=number_format($saldo,0,",",".")?></td></tr><?php $i++;} ?></tbody></table></div></div></div></div></div></div></div></div>