div class="form-group position-relative"><label for="exampleIdKelas">Kelas</label> <select class="form-control"name="id_kelas"><?php $id_kelas=$this->db->get('tb_kelas')->result(); ?><?php foreach($id_kelas as $ro): ?><?php $selected=($r->id_kelas==$ro->id_kelas)?"selected":"" ?><option value="<?=$ro->id_kelas?>"<?php echo $selected; ?>><?=$ro->kelas?></option><?php endforeach  ?></select><?php echo form_error('id_kelas'); ?></div></div></div><div class="form-row"><div class="col-md-6"><div class="form-group position-relative"><label for="exampleIdJurusan">JURUSAN</label> <select class="form-control"name="id_jurusan"><?php $id_jurusan=$this->db->get('tb_jurusan')->result(); ?><option>-- PILIH JURUSAN --</option><?php foreach($id_jurusan as $row): ?><?php $selected=($r->id_jurusan==$row->id_jurusan)?"selected":"" ?><option value="<?=$row->id_jurusan?>"<?php echo $selected; ?>><?=$row->jurusan?></option><?php endforeach  ?></select><?php echo form_error('id_jurusan'); ?></div></div></div><a class="btn btn-sm mt-2 btn-warning"href="<?php echo base_url('siswa/index'); ?>"><i class="fas fa-arrow-left"></i> Kembali</a> <button class="btn btn-sm mt-2 btn-primary"><i class="fas fa-save"></i> Simpan Data Siswa</button><?php echo form_close(); ?></div></div><?php } ?></div></div></div>