<div class="app-main__outer"><div class="app-main__inner"><div class="col-lg-4"><?php echo $this->session->flashdata('pesan'); ?></div><h5>Data Tabungan Siswa</h5><div class="row"><div class="col-lg-12"><div class="card main-card mb-3"><div class="card-body"><h5 class="card-title"><div class="search-wrapper"><div class="row"><div class="col-md-5"><p>Pencarian Data Tabungan Siswa</p><?php echo form_open('tabungan/cari_data') ?><div class="form-row"><div class="col-md-6"><div class="form-group position-relative"><input class="form-control"name="keyword"placeholder="Nama Siswa.."required></div></div><div class="col-md-3"><div class="form-group position-relative"><button class="btn btn-sm btn-success"type="submit">Cari <i class="fa fa-search"></i></button></div></div></div><?php echo form_close(); ?></div></div><a href="<?php echo base_url('tabungan/tambah'); ?>"class="btn btn-sm btn-primary"style="margin-bottom:3px;font-size:12px"><i class="fa-plus fas"></i> Tambah Data siswa</a> <a href="<?php echo base_url('tabungan/index'); ?>"class="btn btn-sm btn-warning"style="margin-bottom:3px;font-size:12px"><i class="pe-7s-refresh-2"title="Refresh Page"></i> Refresh</a></h5><div class="table-responsive"><table class="mb-0 table table-bordered table-hover"><thead><tr><th style="font-size:12px">NO</th><th style="font-size:12px">REKENING</th><th style="font-size:12px">NAMA</th><th style="font-size:12px">KELAS</th><th style="font-size:12px">JURUSAN</th><th style="font-size:12px">JENIS KELAMIN</th><th style="font-size:12px">JUMLAH NABUNG</th><th style="font-size:12px">WAKTU</th><th style="font-size:12px">STATUS</th><th style="font-size:12px">ACTION</th></tr></thead><tbody><?php $i=1;foreach($datas as $r){ ?><tr><td style="font-size:12px"><?php echo $i; ?></td><td style="font-size:12px"><?php echo $r->nore; ?></td><td style="font-size:12px"><?php echo $r->nama_siswa; ?></td><?php if(empty($r->kelas)){ ?><td style="font-size:12px"><?php echo " -"; ?></td><?php }elseif($r->kelas){ ?><td style="font-size:12px"><?php echo $r->kelas; ?></td><?php } ?><?php if(empty($r->jurusan)){ ?><td style="font-size:12px"><?php echo "-"; ?></td><?php }elseif($r->jurusan){ ?><td style="font-size:12px"><?php echo $r->jurusan; ?></td><?php } ?><td style="font-size:12px"><?php echo $r->jk; ?></td><td style="font-size:12px"><?php echo $r->jumlah_nabung; ?></td><td style="font-size:12px"><?php echo $r->waktu; ?></td><td style="font-size:12px"><?php echo $r->status_nabung; ?></td><td style="font-size:12px"><a href="<?php echo base_url('siswa/edit/'.$r->id_tabungan); ?>"><i class="fa fa-pen"title="Edit Data Siswa"></i></a> | <a href="<?php echo base_url('siswa/hapus/'.$r->id_tabungan); ?>"onclick='return confirm("Apakah anda ingin menghapus data siswa")'><i class="fa fa-trash"title="Hapus Data Siswa"></i></a> | <a href="<?php echo base_url('siswa/detail_siswa/'.$r->id_tabungan); ?>"><i class="fa fa-eye"title="Detail Data Siswa"></i></a></td></tr><?php $i++;} ?></tbody></table></div></div></div></div></div></div>